module.exports = function (config) {
  return function (arg) {
    const work = require(`../../../../${config.functionName}`)(arg);
    return config.shouldntWrap
      ? work
      : work
        .then(result => Promise.resolve({
          data: result
        }))
        .catch(error => Promise.reject({
          componentRejection: error
        }));
  };
};
