const stackTrace = require('stack-trace');
const stdPath = require('path');

module.exports = {
  getResolver: function () {
    return require('../../stratHijack')(getCallerService(stackTrace.get()));
  },
  getHijack: function () {
    return require('../../stratHijack');
  }
}

function getCallerService (stack) {
  let relevantStack = stack.slice(1);

  return relevantStack.map(frame => {
    const file = frame.getFileName();
    if (file === null) return;
    const fileTokens = file.split('/');
    return fileTokens[fileTokens.length - 2].split('.')[0];
  })[0];
};
